import cv2import numpy as npfrom detectBlur import preprocessing as preimage= pre.readImages()resize = pre.resize(image)height = np.size(resize, 0)width = np.size(resize, 1)gray = pre.filterGray(resize)blur = pre.filterGaus(gray)deteksiTepi = pre.sobel(blur)deteksiBlur = pre.blurDetection(deteksiTepi, height, width)cv2.imwrite('hasil/hasil.png',deteksiBlur)pre.show(blur, deteksiTepi, deteksiBlur)